import React, {useState, useEffect} from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Alert,
  ScrollView,
  Image,
  TextInput,
  Modal,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface User {
  id: string;
  name: string;
  email: string;
  role: string;
  profileImage?: string;
}

const ProfileScreen = ({navigation}: any) => {
  const [user, setUser] = useState<User | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState('');
  const [editEmail, setEditEmail] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [language, setLanguage] = useState('es');

  useEffect(() => {
    loadUserData();
    loadSettings();
  }, []);

  const loadUserData = async () => {
    try {
      const userData = await AsyncStorage.getItem('user');
      if (userData) {
        const userObj = JSON.parse(userData);
        setUser(userObj);
        setEditName(userObj.name);
        setEditEmail(userObj.email);
      }
    } catch (error) {
      console.error('Error loading user data:', error);
    }
  };

  const loadSettings = async () => {
    try {
      const darkMode = await AsyncStorage.getItem('darkMode');
      const lang = await AsyncStorage.getItem('language');
      
      if (darkMode) {
        setIsDarkMode(JSON.parse(darkMode));
      }
      if (lang) {
        setLanguage(lang);
      }
    } catch (error) {
      console.error('Error loading settings:', error);
    }
  };

  const saveUserData = async () => {
    if (!editName.trim() || !editEmail.trim()) {
      Alert.alert('Error', 'Por favor completa todos los campos');
      return;
    }

    const updatedUser = {
      ...user,
      name: editName.trim(),
      email: editEmail.trim(),
    };

    try {
      await AsyncStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setIsEditing(false);
      Alert.alert('xito', 'Perfil actualizado correctamente');
    } catch (error) {
      console.error('Error saving user data:', error);
      Alert.alert('Error', 'No se pudo actualizar el perfil');
    }
  };

  const toggleDarkMode = async () => {
    const newDarkMode = !isDarkMode;
    setIsDarkMode(newDarkMode);
    try {
      await AsyncStorage.setItem('darkMode', JSON.stringify(newDarkMode));
    } catch (error) {
      console.error('Error saving dark mode setting:', error);
    }
  };

  const changeLanguage = async (newLanguage: string) => {
    setLanguage(newLanguage);
    try {
      await AsyncStorage.setItem('language', newLanguage);
    } catch (error) {
      console.error('Error saving language setting:', error);
    }
  };

  const logout = () => {
    Alert.alert(
      'Cerrar Sesi贸n',
      '驴Est谩s seguro de que quieres cerrar sesi贸n?',
      [
        {text: 'Cancelar', style: 'cancel'},
        {
          text: 'Cerrar Sesi贸n',
          style: 'destructive',
          onPress: async () => {
            try {
              await AsyncStorage.clear();
              navigation.navigate('Login');
            } catch (error) {
              console.error('Error during logout:', error);
            }
          },
        },
      ]
    );
  };

  const showLanguageModal = () => {
    Alert.alert(
      'Seleccionar Idioma',
      'Elige tu idioma preferido',
      [
        {text: 'Espa帽ol', onPress: () => changeLanguage('es')},
        {text: 'English', onPress: () => changeLanguage('en')},
        {text: '涓', onPress: () => changeLanguage('zh')},
        {text: 'Cancelar', style: 'cancel'},
      ]
    );
  };

  if (!user) {
    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.headerTitle}> Perfil</Text>
        </View>
        <View style={styles.loadingContainer}>
          <Text style={styles.loadingText}>Cargando perfil...</Text>
        </View>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}> Perfil</Text>
      </View>

      {/* Profile Info */}
      <View style={styles.profileSection}>
        <View style={styles.avatarContainer}>
          <Text style={styles.avatarText}>
            {user.name.charAt(0).toUpperCase()}
          </Text>
        </View>
        
        <Text style={styles.userName}>{user.name}</Text>
        <Text style={styles.userEmail}>{user.email}</Text>
        <Text style={styles.userRole}>
          {user.role === 'admin' ? 'Administrador' : 'Cliente'}
        </Text>
      </View>

      {/* Edit Profile */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Informaci贸n Personal</Text>
        
        {isEditing ? (
          <View style={styles.editForm}>
            <View style={styles.inputContainer}>
              <Text style={styles.label}>Nombre</Text>
              <TextInput
                style={styles.input}
                value={editName}
                onChangeText={setEditName}
                placeholder="Tu nombre"
              />
            </View>
            
            <View style={styles.inputContainer}>
              <Text style={styles.label}>Email</Text>
              <TextInput
                style={styles.input}
                value={editEmail}
                onChangeText={setEditEmail}
                placeholder="tu@email.com"
                keyboardType="email-address"
              />
            </View>
            
            <View style={styles.editButtons}>
              <TouchableOpacity
                style={styles.cancelButton}
                onPress={() => setIsEditing(false)}>
                <Text style={styles.cancelButtonText}>Cancelar</Text>
              </TouchableOpacity>
              
              <TouchableOpacity
                style={styles.saveButton}
                onPress={saveUserData}>
                <Text style={styles.saveButtonText}>Guardar</Text>
              </TouchableOpacity>
            </View>
          </View>
        ) : (
          <TouchableOpacity
            style={styles.editButton}
            onPress={() => setIsEditing(true)}>
            <Text style={styles.editButtonText}>Editar Perfil</Text>
          </TouchableOpacity>
        )}
      </View>

      {/* Settings */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Configuraci贸n</Text>
        
        <TouchableOpacity style={styles.settingItem} onPress={toggleDarkMode}>
          <Text style={styles.settingLabel}>Modo Oscuro</Text>
          <Text style={styles.settingValue}>
            {isDarkMode ? 'Activado' : 'Desactivado'}
          </Text>
        </TouchableOpacity>
        
        <TouchableOpacity style={styles.settingItem} onPress={showLanguageModal}>
          <Text style={styles.settingLabel}>Idioma</Text>
          <Text style={styles.settingValue}>
            {language === 'es' ? 'Espa帽ol' : language === 'en' ? 'English' : '涓'}
          </Text>
        </TouchableOpacity>
      </View>

      {/* Admin Options */}
      {user.role === 'admin' && (
        <View style={styles.section}>
